        int u, v;
        cin >> u >> v;
        u--; v--;
        graph[u].push_back({v, 1});
        graph[v].push_back({u, 1});
    }

    vector<int> parent(N, -1);
    vector<int> size(N, 1);
    vector<int> order(N);

    function<void(int)> dfs = [&](int u) {
        order[u] = N - 1;
        for (auto e : graph[u]) {
            int v = e.to;
            if (v == parent[u]) continue;
            parent[v] = u;
            size[u] += size[v];
            dfs(v);
            order[u] = min(order[u], order[v]);
        }
    };

    dfs(0);

    vector<long long> dp(N);
    dp[0] = 1;
    for (int i = 0; i < N; i++) {
        int u = order[i];
        for (auto e : graph[u]) {
            int v = e.to;
            if (v == parent[u]) continue;
            dp[u] = (dp[u] + dp[v] * size[v]) % MOD;
        }
        long long sum = 0;
        for (auto e : graph[u]) {
            int v = e.to;
            if (v == parent[u]) continue;
            sum = (sum + dp[v] * size[v]) % MOD;
        }
        dp[u] = (dp[u] + sum * size[u]) % MOD;
        cout << (dp[u] * (N - i)) % MOD << endl;
    }

    return 0;
