import sys
input = sys.stdin.readline

mod = 924844033
N = 200000

class UnionFind:
    def __init__(self, n):
        self.parent = list(range(n))
        self.size = [1] * n

    def find(self, a):
        acopy = a
        while a != self.parent[a]:
            a = self.parent[a]
        while acopy != a:
            self.parent[acopy], acopy = a, self.parent[acopy]
        return a

    def union(self, a, b):
        a, b = self.find(a), self.find(b)
        if a == b:
            return
        if self.size[a] < self.size[b]:
            a, b = b, a
        self.parent[b] = a
        self.size[a] += self.size[b]

def main():
    n = int(input())
    edges = [tuple(map(int, input().split())) for _ in range(n - 1)]
    uf = UnionFind(n + 1)
    for a, b in edges:
        uf.union(a, b)
    parent = [uf.find(i) for i in range(1, n + 1)]
    size = [uf.size[i] for i in range(1, n + 1)]
    ans = [0] * (n + 1)
    for i in range(1, n + 1):
        ans[i] = pow(2, size[i] - 1, mod)
    for i in range(1, n + 1):
        for j in range(i):
            ans[i] = (ans[i] - ans[j] * size[i] + mod) % mod
    for i in range(1, n + 1):
        print((ans[i] * i) % mod)

