import java.io.*;
import java.util.*;

public class Main {
    static final int MOD = 924844033;
    static int N;
    static List<Integer>[] G;
    static int[] sz;
    static long[] ans;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        StringTokenizer st;

        N = Integer.parseInt(br.readLine());
        G = new List[N];
        for (int i = 0; i < N; i++) {
            G[i] = new ArrayList<>();
        }
        for (int i = 0; i < N - 1; i++) {
            st = new StringTokenizer(br.readLine());
            int u = Integer.parseInt(st.nextToken()) - 1;
            int v = Integer.parseInt(st.nextToken()) - 1;
            G[u].add(v);
            G[v].add(u);
        }

        sz = new int[N];
        ans = new long[N + 1];
        dfs(0, -1);
        for (int i = 1; i <= N; i++) {
            ans[i] = (i * (long) ans[i]) % MOD;
            pw.println(ans[i]);
        }
        pw.close();
    }

    static void dfs(int u, int p) {
        sz[u] = 1;
        for (int v : G[u]) {
            if (v != p) {
                dfs(v, u);
                sz[u] += sz[v];
            }
        }
        for (int i = 1; i <= N; i++) {
            ans[i] = (ans[i] + (long) sz[u] * (sz[u] - 1) / 2 % MOD * C(i - 1, N - sz[u]) % MOD) % MOD;
        }
    }

    static long C(int n, int k) {
        long res = 1;
        for (int i = 0; i < k; i++) {
            res = res * (n - i) % MOD * pow(i + 1, MOD - 2) % MOD;
        }
        return res;
    }

    static long pow(long a, long b) {
        long res = 1;
        while (b > 0) {
            if ((b & 1) == 1) {
                res = res * a % MOD;
            }
            a = a * a % MOD;
            b >>= 1;
        }
        return res;
    }
}
