def solve(s, k):
    n = len(s)
    ans = list(s)
    for i in range(n):
        if k == 0:
            break
        if ans[i] == 'z':
            ans[i] = 'a'
        else:
            ans[i] = chr(ord(ans[i]) + 1)
            k -= 1
    for i in range(n - 1, -1, -1):
        if k >= 26:
            k -= 26
            ans[i] = 'a'
        elif k > 0:
            ans[i] = chr(ord(ans[i]) + k)
            k = 0
    return ''.join(ans)

