                c.erase(c.begin());
            } else if (b.size() > 0) {
                b.erase(b.begin());
            }
        } else {
            if (b.size() > 0) {
                b.erase(b.begin());
            } else if (c.size() > 0) {
                c.erase(c.begin());
            }
        }
        i++;
    }

    if (a.size() == i) {
        std::cout << "A" << std::endl;
    } else if (b.size() == 0 && c.size() == 0) {
        std::cout << "B" << std::endl;
    } else {
        std::cout << "C" << std::endl;
    }

    return 0;
}
