def dfs(s, i, j, memo):
    if i == j:
        return int(s[i-1])
    if (i, j) in memo:
        return memo[(i, j)]
    res = 0
    for k in range(i, j):
        if k == i:
            res += dfs(s, i+1, j, memo)
        else:
            res += dfs(s, i, k, memo) * dfs(s, k+1, j, memo)
        if k < j and s[k] == '+':
            res += dfs(s, i, k, memo) + dfs(s, k+1, j, memo)
    memo[(i, j)] = res
    return res

def sum_of_evaluated_values(s):
    return dfs(s, 1, len(s), {})

