            }
        }
        ans += eval(s);
        System.out.println(ans);
    }

    public static long eval(String s) {
        long ans = 0;
        int i = 0;
        while (i < s.length()) {
            int j = i;
            while (j < s.length() && s.charAt(j) != '+') {
                j++;
            }
            if (j == i) {
                ans += Long.parseLong(s.substring(i));
                i = j + 1;
            } else {
                ans += eval(s.substring(i, j));
                i = j + 1;
            }
        }
        return ans;
    }
}
