import java.io.*;
import java.util.*;

public class Main {
    static class FastReader {
        BufferedReader br;
        StringTokenizer st;

        public FastReader() {
            br = new BufferedReader(new InputStreamReader(System.in));
        }

        String next() {
            while (st == null || !st.hasMoreElements()) {
                try {
                    st = new StringTokenizer(br.readLine());
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
            return st.nextToken();
        }

        int nextInt() {
            return Integer.parseInt(next());
        }

        long nextLong() {
            return Long.parseLong(next());
        }
    }

    static class Point {
        int x, y;

        public Point(int x, int y) {
            this.x = x;
            this.y = y;
        }
    }

    static class Rect {
        int x, y, w, h;

        public Rect(int x, int y, int w, int h) {
            this.x = x;
            this.y = y;
            this.w = w;
            this.h = h;
        }
    }

    static class FenwickTree {
        int n;
        long[] tree;

        public FenwickTree(int n) {
            this.n = n;
            tree = new long[n + 1];
        }

        void add(int i, long val) {
            for (i++; i <= n; i += i & -i) {
                tree[i] += val;
            }
        }

        long sum(int i) {
            long res = 0;
            for (i++; i > 0; i -= i & -i) {
                res += tree[i];
            }
            return res;
        }

        long sum(int l, int r) {
            return sum(r) - sum(l - 1);
        }
    }

    public static void main(String[] args) {
        FastReader in = new FastReader();
        PrintWriter out = new PrintWriter(System.out);

        int H = in.nextInt();
        int W = in.nextInt();
        int N = in.nextInt();

        int[][] a = new int[N][2];
        for (int i = 0; i < N; i++) {
            a[i][0] = in.nextInt();
            a[i][1] = in.nextInt();
        }

        int[][] cnt = new int[10][H + W + 1];
        FenwickTree[] ft = new FenwickTree[10];
        for (int i = 0; i < 10; i++) {
            ft[i] = new FenwickTree(H + W + 1);
        }

        for (int i = 0; i < N; i++) {
            int x = a[i][0];
            int y = a[i][1];
            for (int j = 0; j < 10; j++) {
                if (j == 0) {
                    ft[j].add(x, 1);
                    ft[j].add(y, 1);
                } else {
                    int val = ft[j - 1].sum(x - 1, y - 1);
                    ft[j].add(x, val);
                    ft[j].add(y, val);
                }
                cnt[j][ft[j].sum(x, y)]++;
            }
        }

        for (int i = 0; i < 10; i++) {
            for (int j = 0; j < H + W + 1; j++) {
                ft[i].add(j, -cnt[i][j]);
            }
        }

        for (int i = 0; i < 10; i++) {
            out.println(ft[i].sum(0, H + W));
        }

        out.close();
    }
}
