        for (int i = 1; i <= n; i++) {
            dp[i] = dp[i - 1];
            for (int j = 1; j <= i; j++) {
                if (s.charAt(i - 1) - '0' >= s.charAt(j - 1) - '0') {
                    dp[i] += dp[i - j];
                }
            }
        }
        long ans = 0;
        for (int i = 1; i <= n; i++) {
            long sum = 0;
            for (int j = 1; j <= i; j++) {
                if (s.charAt(i - 1) - '0' >= s.charAt(j - 1) - '0') {
                    sum += sumOfDigits(s.substring(j - 1, i));
                }
            }
            ans += sum * dp[i];
        }
        System.out.println(ans);
    }

    public static long sumOfDigits(String s) {
        long sum = 0;
        for (char c : s.toCharArray()) {
            sum += c - '0';
        }
        return sum;
    }
}
