using namespace std;

const int INF = numeric_limits<int>::max();

int main() {
    int n, m;
    cin >> n >> m;

    vector<vector<pair<int, int>>> graph(n + 1);
    unordered_map<int, vector<int>> company;

    for (int i = 0; i < m; i++) {
        int p, q, c;
        cin >> p >> q >> c;
        graph[p].push_back({q, c});
        graph[q].push_back({p, c});
        company[c].push_back(p);
        company[c].push_back(q);
    }

    vector<int> dist(n + 1, INF);
    dist[1] = 0;

    priority_queue<pair<int, int>, vector<pair<int, int>>, greater<pair<int, int>>> pq;
    pq.push({0, 1});

    while (!pq.empty()) {
        int d = pq.top().first;
        int u = pq.top().second;
        pq.pop();

        if (d > dist[u]) continue;

        for (auto& [v, c] : graph[u]) {
            if (dist[v] > dist[u] + 1) {
                dist[v] = dist[u] + 1;
                pq.push({dist[v], v});
            }

            if (company[c].size() > 1 && dist[v] > dist[u] + company[c].size() - 1) {
                dist[v] = dist[u] + company[c].size() - 1;
                pq.push({dist[v], v});
            }
        }
    }

    if (dist[n] == INF) cout << -1 << endl;
    else cout << dist[n] - 1 << endl;

    return 0;
}
