import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.io.PrintWriter;
import java.util.*;

public class Main {
    static class FastReader {
        BufferedReader br;
        StringTokenizer st;

        public FastReader() {
            br = new BufferedReader(new InputStreamReader(System.in));
        }

        String next() {
            while (st == null || !st.hasMoreElements()) {
                try {
                    st = new StringTokenizer(br.readLine());
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
            return st.nextToken();
        }

        int nextInt() {
            return Integer.parseInt(next());
        }

        long nextLong() {
            return Long.parseLong(next());
        }

        double nextDouble() {
            return Double.parseDouble(next());
        }

        String nextLine() {
            String str = "";
            try {
                str = br.readLine();
            } catch (IOException e) {
                e.printStackTrace();
            }
            return str;
        }
    }

    static class Pair implements Comparable<Pair> {
        int first;
        int second;

        public Pair(int first, int second) {
            this.first = first;
            this.second = second;
        }

        @Override
        public int compareTo(Pair o) {
            return Integer.compare(this.first, o.first);
        }
    }

    public static void main(String[] args) {
        FastReader sc = new FastReader();
        PrintWriter out = new PrintWriter(System.out);
        int n = sc.nextInt();
        int m = sc.nextInt();
        List<List<Pair>> graph = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            graph.add(new ArrayList<>());
        }
        for (int i = 0; i < m; i++) {
            int p = sc.nextInt();
            int q = sc.nextInt();
            int c = sc.nextInt();
            graph.get(p - 1).add(new Pair(q - 1, c));
            graph.get(q - 1).add(new Pair(p - 1, c));
        }
        int[] parent = new int[n];
        Arrays.fill(parent, -1);
        int[] cost = new int[n];
        Arrays.fill(cost, Integer.MAX_VALUE);
        cost[0] = 0;
        PriorityQueue<Pair> pq = new PriorityQueue<>();
        pq.add(new Pair(0, 0));
        while (!pq.isEmpty()) {
            Pair pair = pq.poll();
            int node = pair.second;
            int c = pair.first;
            if (cost[node] != c) continue;
            for (Pair neighbor : graph.get(node)) {
                int nextNode = neighbor.first;
                int nextCompany = neighbor.second;
                if (nextCompany == parent[node]) {
                    cost[nextNode] = Math.min(cost[nextNode], c + 1);
                    pq.add(new Pair(cost[nextNode], nextNode));
                } else if (parent[nextNode] != nextCompany) {
                    parent[nextNode] = nextCompany;
                    cost[nextNode] = Math.min(cost[nextNode], c + 1);
                    pq.add(new Pair(cost[nextNode], nextNode));
                }
            }
        }
        if (cost[n - 1] == Integer.MAX_VALUE) {
            out.println(-1);
        } else {
            out.println(cost[n - 1]);
        }
        out.close();
    }
}
