#include <iostream>
#include <vector>

const int MOD = 1e9 + 7;

int main() {
    int N, M, K;
    std::cin >> N >> M >> K;

    std::vector<std::vector<std::vector<long long>>> dp(N + 1, std::vector<std::vector<long long>>(M + 1, std::vector<long long>(K + 1, 0)));

    dp[0][0][0] = 1;

    for (int n = 0; n <= N; n++) {
        for (int m = 0; m <= M; m++) {
            for (int k = 0; k <= K; k++) {
                if (n == N && m == M && k == K) continue;

                long long cur = dp[n][m][k];

                if (n < N) {
                    dp[n + 1][m][k] = (dp[n + 1][m][k] + 3 * cur) % MOD;
                }

                if (m < M) {
                    dp[n][m + 1][k] = (dp[n][m + 1][k] + 3 * cur) % MOD;
                }

                if (k < K) {
                    dp[n][m][k + 1] = (dp[n][m][k + 1] + 3 * cur) % MOD;
                }

                if (n < N && m < M) {
                    dp[n + 1][m + 1][k] = (dp[n + 1][m + 1][k] + 2 * cur) % MOD;
                }

                if (n < N && k < K) {
                    dp[n + 1][m][k + 1] = (dp[n + 1][m][k + 1] + 2 * cur) % MOD;
                }

                if (m < M && k < K) {
                    dp[n][m + 1][k + 1] = (dp[n][m + 1][k + 1] + 2 * cur) % MOD;
                }
            }
        }
    }

    std::cout << dp[N][M][K] << std::endl;

    return 0;
}
