public class Main {
    static final int MOD = 1000000007;
    static final int MAXN = 300005;

    static long[] fact = new long[MAXN];
    static long[] inv = new long[MAXN];

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);

        fact[0] = 1;
        for (int i = 1; i < MAXN; i++) {
            fact[i] = (fact[i - 1] * i) % MOD;
        }
        inv[MAXN - 1] = pow(fact[MAXN - 1], MOD - 2);
        for (int i = MAXN - 2; i >= 0; i--) {
            inv[i] = (inv[i + 1] * (i + 1)) % MOD;
        }

        StringTokenizer st = new StringTokenizer(br.readLine());
        int n = Integer.parseInt(st.nextToken());
        int m = Integer.parseInt(st.nextToken());
        int k = Integer.parseInt(st.nextToken());

        long ans = (pow(3, n + m + k) - pow(2, n + m + k) + pow(2, n + m + k - 1) * pow(3, n + k - 1) % MOD + pow(2, n + m + k - 1) * pow(3, m + k - 1) % MOD) % MOD;
        pw.println(ans);
        pw.close();
    }

    static long pow(long a, long b) {
        long res = 1;
        while (b > 0) {
            if ((b & 1) == 1) {
                res = (res * a) % MOD;
            }
            a = (a * a) % MOD;
            b >>= 1;
        }
        return res;
    }
}
