    cin >> n >> k;

    vector<int> a(n);
    for (int i = 0; i < n; i++) {
        cin >> a[i];
    }

    unordered_map<int, int> cnt;
    for (int i = 0; i < n; i++) {
        cnt[a[i]]++;
    }

    priority_queue<pair<int, int>> pq;
    for (auto& it : cnt) {
        pq.push({it.second, it.first});
    }

    int ans = 0;
    while (!pq.empty()) {
        auto [cnt, x] = pq.top();
        pq.pop();

        if (cnt == 1) {
            ans += cnt;
            continue;
        }

        if (k % (cnt + 1) == 0) {
            ans += cnt;
            continue;
        }

        if (k % (cnt + 1) == 1) {
            ans += cnt - 1;
            pq.push({cnt - 1, x});
            pq.push({1, x});
            break;
        }

        if (k % (cnt + 1) == 2) {
            ans += cnt - 2;
            pq.push({cnt - 2, x});
            pq.push({2, x});
            break;
        }
    }

    cout << ans << endl;

    return 0;
