#define MAX_H 105
#define MAX_W 105

int h, w;
char grid[MAX_H][MAX_W];
int dp[MAX_H][MAX_W];

int dfs(int x, int y) {
    if (x < 0 || x >= h || y < 0 || y >= w || grid[x][y] == 'E') return 0;
    if (dp[x][y] != -1) return dp[x][y];
    int res = 0;
    if (grid[x][y] == 'o') {
        res = 1 + dfs(x - 1, y) + dfs(x + 1, y) + dfs(x, y - 1) + dfs(x, y + 1);
    } else {
        res = dfs(x - 1, y) + dfs(x + 1, y) + dfs(x, y - 1) + dfs(x, y + 1);
    }
    dp[x][y] = res;
    return res;
}

int main() {
    scanf("%d %d", &h, &w);
    for (int i = 0; i < h; i++) {
        scanf("%s", grid[i]);
    }
    int exit_x, exit_y;
    for (int i = 0; i < h; i++) {
        for (int j = 0; j < w; j++) {
            if (grid[i][j] == 'E') {
                exit_x = i;
                exit_y = j;
            }
        }
    }
    memset(dp, -1, sizeof(dp));
    int res = dfs(exit_x, exit_y);
    printf("%d\n", res - 1);
    return 0;
}
