from collections import deque

def solve(grid):
    h, w = len(grid), len(grid[0])
    directions = [(0, 1), (0, -1), (1, 0), (-1, 0)]
    exit_pos = None
    for i in range(h):
        for j in range(w):
            if grid[i][j] == 'E':
                exit_pos = (i, j)
                grid[i][j] = '.'
                break
        if exit_pos:
            break

    queue = deque()
    visited = [[False]*w for _ in range(h)]
    for i in range(h):
        for j in range(w):
            if grid[i][j] == 'o':
                queue.append((i, j))
                visited[i][j] = True

    max_robots = 0
    while queue:
        x, y = queue.popleft()
        max_robots += 1
        for dx, dy in directions:
            nx, ny = x + dx, y + dy
            if 0 <= nx < h and 0 <= ny < w and not visited[nx][ny]:
                if grid[nx][ny] == 'E':
                    return max_robots
                if grid[nx][ny] == 'o':
                    queue.append((nx, ny))
                    visited[nx][ny] = True

    return max_robots

def main():
    h, w = map(int, input().split())
    grid = [list(input()) for _ in range(h)]
    print(solve(grid))

if __name__ == "__main__":
    main()
