        int ans = 0;
        for (int i = 1; i <= N; i++) {
            ans += cnt[i] % 2;
        }
        System.out.println(ans);
    }

    static int dfs(int u, int fa) {
        color[u] = 1;
        for (int v : g[u]) {
            if (v == fa) continue;
            if (color[v] == 0) {
                cnt[u]++;
                return dfs(v, u);
            } else {
                if ((color[u] + color[v]) % 2 == 0) {
                    cnt[u]--;
                    cnt[v]--;
                } else {
                    cnt[u]++;
                    cnt[v]++;
                }
            }
        }
