#define MOD 1000000007
#define N 500005

typedef struct {
    int len;
    int cnt;
    int next[N];
} AC;

AC ac;
int n, m, cnt, len, ans;
char s[N], t[N];
int f[N][26], g[N][26], h[N][26];

void add(int x, int y) {
    int p = ++cnt;
    ac.next[p] = ac.len + 1;
    for (; p && !f[p][y]; p = f[p][y] = ++cnt) f[p][y] = ac.next[p];
    if (p) g[p][y] = ac.next[p];
    else ac.next[p] = ac.len + 1;
}

void dfs(int x, int y, int z) {
    if (x == ac.len + 1) {
        if (z == 1) ans++;
        else ans = (ans + 1LL * (m - z) * (m - z - 1) / 2 % MOD) % MOD;
        return;
    }
    if (y == -1) {
        dfs(x + 1, -1, z);
        return;
    }
    if (g[x][y] == ac.len + 1) {
        dfs(x + 1, y, z);
        return;
    }
    for (int i = 0; i < 26; i++) {
        if (i != y) {
            dfs(g[x][y], i, z + 1);
        }
    }
}

