            }
        }
    }
    for (int i = 1; i <= ac.len; i++) {
        for (int j = 0; j < 26; j++) {
            if (h[i][j] == ac.len + 1) continue;
            m = 0;
            for (int k = i; k <= ac.len; k++) {
                if (g[k][j] == ac.len + 1) break;
                m++;
            }
            dfs(i, j, 1);
            i = ac.len + 1;
        }
    }
    printf("%d\n%d\n", ac.len, ans);
