#include <iostream>
#include <vector>
#include <string>
#include <algorithm>
#include <unordered_map>

using namespace std;

const int MOD = 1e9 + 7;

int main() {
    string s;
    cin >> s;

    int n = s.size();
    vector<int> cnt(26);
    for (char c : s) {
        cnt[c - 'a']++;
    }

    int max_cnt = 0;
    for (int i = 0; i < 26; i++) {
        max_cnt = max(max_cnt, cnt[i]);
    }

    if (max_cnt == 1) {
        cout << n << endl;
        cout << 1 << endl;
        return 0;
    }

    vector<int> p2(n + 1);
    p2[0] = 1;
    for (int i = 1; i <= n; i++) {
        p2[i] = (p2[i - 1] * 2) % MOD;
    }

    unordered_map<string, int> dp;
    function<int(string)> dfs = [&](string s) {
        if (dp.count(s)) {
            return dp[s];
        }

        int res = 1;
        for (int i = 0; i < 26; i++) {
            if (cnt[i] > 0) {
                string t = s;
                for (int j = 0; j < cnt[i]; j++) {
                    t += (char)('a' + i);
                }
                res = (res + dfs(t)) % MOD;
            }
        }

        dp[s] = res;
        return res;
    };

    int ans = dfs(s);
    cout << n - max_cnt + 1 << endl;
    cout << ans << endl;

    return 0;
}
