#include <stdio.h>
#include <string.h>

int main() {
    char s[1001];
    scanf("%s", s);
    int n = strlen(s);
    int x = 0, y = 0;
    for (int i = 0; i < n; i++) {
        switch (s[i]) {
            case 'N':
                y++;
                break;
            case 'S':
                y--;
                break;
            case 'E':
                x++;
                break;
            case 'W':
                x--;
                break;
        }
    }
    if (x == 0 && y == 0) {
        printf("Yes\n");
    } else {
        printf("No\n");
    }
    return 0;
}
