#include <stack>

using namespace std;

int main() {
    string s;
    cin >> s;

    stack<char> st;
    int x = 0, y = 0;

    for (char c : s) {
        if (c == 'N') {
            st.push('N');
            y++;
        } else if (c == 'S') {
            if (st.empty() || st.top() != 'N') {
                cout << "No" << endl;
                return 0;
            }
            st.pop();
            y--;
        } else if (c == 'E') {
            st.push('E');
            x++;
        } else if (c == 'W') {
            if (st.empty() || st.top() != 'E') {
                cout << "No" << endl;
                return 0;
            }
            st.pop();
            x--;
        }
    }

    if (st.empty()) {
        cout << "Yes" << endl;
    } else {
        cout << "No" << endl;
    }

    return 0;
}
