def is_possible_to_return_home(S):
    x, y = 0, 0
    for s in S:
        if s == 'N':
            y += 1
        elif s == 'S':
            y -= 1
        elif s == 'E':
            x += 1
        else:
            x -= 1
    return x == 0 and y == 0

