                int j = i;
                while (j < n - 1 && a[j] > a[j + 1]) {
                    j++;
                }
                int k = j;
                while (k < n - 1 && a[k] < a[k + 1]) {
                    k++;
                }
                ans += (j - i + 1) / 2;
                reverse(a, i, j);
                reverse(a, j, k);
            }
        }
        System.out.println(ans);
    }

    static void reverse(int[] a, int l, int r) {
        while (l < r) {
            int temp = a[l];
            a[l] = a[r];
            a[r] = temp;
            l++;
            r--;
        }
    }
