int dfs(int x, int y, int h, int w, char grid[MAX][MAX], int visited[MAX][MAX]) {
    if (x < 0 || x >= h || y < 0 || y >= w || grid[x][y] == '.' || visited[x][y]) {
        return 0;
    }
    visited[x][y] = 1;
    int count = 1;
    for (int i = 0; i < 4; i++) {
        count += dfs(x + dx[i], y + dy[i], h, w, grid, visited);
    }
    return count;
}

