    char grid[MAX][MAX];
    for (int i = 0; i < h; i++) {
        scanf("%s", grid[i]);
    }
    int visited[MAX][MAX];
    for (int i = 0; i < h; i++) {
        for (int j = 0; j < w; j++) {
            visited[i][j] = 0;
        }
    }
    int count = 0;
    for (int i = 0; i < h; i++) {
        for (int j = 0; j < w; j++) {
            if (grid[i][j] == '#' && !visited[i][j]) {
                count += dfs(i, j, h, w, grid, visited);
            }
        }
    }
