public class Main {
    static final int MOD = 1000000007;
    static int[][] dir = {{0, 1}, {0, -1}, {1, 0}, {-1, 0}};
    static int[][] grid;
    static int[][] vis;
    static int n, m;

    static int dfs(int x, int y) {
        if (x < 0 || x >= n || y < 0 || y >= m || vis[x][y] == 1 || grid[x][y] == '.') return 0;
        vis[x][y] = 1;
        int res = 1;
        for (int[] d : dir) {
            res += dfs(x + d[0], y + d[1]);
        }
        return res;
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        StringTokenizer st = new StringTokenizer(br.readLine());
        n = Integer.parseInt(st.nextToken());
        m = Integer.parseInt(st.nextToken());
        long k = Long.parseLong(st.nextToken());
        grid = new int[n][m];
        vis = new int[n][m];
        for (int i = 0; i < n; i++) {
            String s = br.readLine();
            for (int j = 0; j < m; j++) {
                grid[i][j] = s.charAt(j) == '#' ? 1 : 0;
            }
        }
        long ans = 0;
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < m; j++) {
                if (grid[i][j] == 1 && vis[i][j] == 0) {
                    ans += dfs(i, j);
                }
            }
        }
        long[] pow2 = new long[1001];
        pow2[0] = 1;
        for (int i = 1; i <= 1000; i++) {
            pow2[i] = (pow2[i - 1] * 2) % MOD;
        }
        long[] pow4 = new long[1001];
        pow4[0] = 1;
        for (int i = 1; i <= 1000; i++) {
            pow4[i] = (pow4[i - 1] * 4) % MOD;
        }
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < m; j++) {
                if (grid[i][j] == 1) {
                    ans = (ans + pow4[(int) k]) % MOD;
                    k--;
                    while (k >= 0 && grid[i][j] == 1) {
                        ans = (ans + pow2[(int) k]) % MOD;
                        k--;
                    }
                }
            }
        }
        System.out.println(ans);
    }
