#include <stdio.h>
#include <stdlib.h>
#include <math.h>

#define MOD 1000000007
#define N 405

long long pow(long long a, long long b) {
    long long res = 1;
    while (b) {
        if (b & 1) res = res * a % MOD;
        a = a * a % MOD;
        b >>= 1;
    }
    return res;
}

long long inv(long long a) {
    return pow(a, MOD - 2);
}

int main() {
    int n, c;
    scanf("%d%d", &n, &c);
    int a[N], b[N];
    for (int i = 1; i <= n; i++) scanf("%d", &a[i]);
    for (int i = 1; i <= n; i++) scanf("%d", &b[i]);
    long long f[N][N], g[N][N];
    for (int i = 0; i <= n; i++) {
        for (int j = 0; j <= n; j++) {
            f[i][j] = 0;
            g[i][j] = 0;
        }
    }
    for (int i = 0; i <= n; i++) {
        f[i][0] = 1;
        g[i][0] = 1;
    }
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= n; j++) {
            f[i][j] = (f[i - 1][j - 1] * b[i] + f[i - 1][j] * a[i]) % MOD;
            g[i][j] = (g[i - 1][j - 1] * b[i] + g[i - 1][j] * a[i]) % MOD;
        }
    }
    long long ans = 0;
    for (int i = 0; i <= c; i++) {
        for (int j = 0; j <= c; j++) {
            if (i + j == c) {
                long long res = 1;
                for (int k = 1; k <= n; k++) {
                    res = res * pow(a[k], i) % MOD * pow(b[k], j) % MOD;
                }
                ans = (ans + res * f[n][i] % MOD * g[n][j] % MOD) % MOD;
            }
        }
    }
    printf("%lld\n", ans);
    return 0;
}
