MOD = 10**9 + 7

def power(x, y):
    res = 1
    while y:
        if y & 1:
            res = (res * x) % MOD
        x = (x * x) % MOD
        y >>= 1
    return res

def solve():
    n, c = map(int, input().split())
    a = list(map(int, input().split()))
    b = list(map(int, input().split()))

    dp = [[0] * (c + 1) for _ in range(n + 1)]
    for i in range(n + 1):
        dp[i][0] = 1

    for i in range(1, n + 1):
        for j in range(1, min(i, c) + 1):
            for k in range(1, min(b[i - 1], j) + 1):
                dp[i][j] = (dp[i][j] + dp[i - 1][j - k] * power(a[i - 1], k) % MOD) % MOD

    ans = 0
    for i in range(1, c + 1):
        ans = (ans + dp[n][i]) % MOD

    print(ans)

if __name__ == "__main__":
