```java
import java.io.*;
import java.util.*;

public class Main {
    static final int MOD = 1000000007;
    static final int MAXN = 405;

    static long[][] pow = new long[MAXN][MAXN];
    static long[] inv = new long[MAXN];

    public static void main(String[] args) throws IOException {
        Scanner sc = new Scanner(System.in);
        PrintWriter pw = new PrintWriter(System.out);

        int n = sc.nextInt();
        int c = sc.nextInt();

        for (int i = 0; i <= n; i++) {
            pow[i][0] = 1;
            for (int j = 1; j <= n; j++) {
                pow[i][j] = pow[i][j - 1] * i % MOD;
            }
        }

        for (int i = 1; i <= n; i++) {
            inv[i] = qpow(i, MOD - 2);
        }

        long[][] dp = new long[n + 1][c + 1];
        for (int i = 0; i <= n; i++) {
            for (int j = 0; j <= c; j++) {
                dp[i][j] = 0;
            }
        }

        for (int i = 1; i <= n; i++) {
            for (int j = 0; j <= c; j++) {
                if (j == 0) {
                    dp[i][j] = pow[i][0];
                } else {
                    dp[i][j] = (dp[i - 1][j] + pow[i][j] * dp[i - 1][j - 1] % MOD) % MOD;
                }
            }
        }

        long ans = 0;
        for (int i = 0; i <= c; i++) {
            long tmp = 1;
            for (int j = 1; j <= n; j++) {
                tmp = tmp * (sc.nextInt() + c - i) % MOD * (sc.nextInt() - c + i + 1) % MOD;
            }
            ans = (ans + tmp * dp[n][i] % MOD) % MOD;
        }

        pw.println(ans);
        pw.close();
    }

    static long qpow(long a, long b) {
        long res = 1;
        while (b > 0) {
            if ((b & 1) == 1) {
                res = res * a % MOD;
            }
            a = a * a % MOD;
            b >>= 1;
        }
        return res;
    }
}

