#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <math.h>

#define N 100005
#define M 100005
#define Q 100005

typedef struct {
    int to, next;
} Edge;

typedef struct {
    int head[N], e[M], num;
} Graph;

void addEdge(Graph *g, int u, int v) {
    g->e[g->num] = v;
    g->head[u] = g->num++;
}

void dfs(Graph *g, int u, int fa, int *d, int *f) {
    for (int i = g->head[u]; i != -1; i = g->head[u]) {
        int v = g->e[i];
        if (v == fa) continue;
        d[v] = d[u] + 1;
        f[v] = u;
        dfs(g, v, u, d, f);
    }
}

int lca(Graph *g, int u, int v, int *d, int *f) {
    if (d[u] < d[v]) {
        int temp = u;
        u = v;
        v = temp;
    }
    while (d[u] > d[v]) {
        u = g->f[u];
    }
    if (u == v) return u;
    while (g->f[u] != g->f[v]) {
        u = g->f[u];
        v = g->f[v];
    }
    return g->f[u];
}

int main() {
    Graph g;
    g.num = -1;
    for (int i = 0; i < N; i++) g.head[i] = -1;
    int n, m, q;
    scanf("%d%d", &n, &m);
    for (int i = 1; i <= m; i++) {
        int u, v;
        scanf("%d%d", &u, &v);
        addEdge(&g, u, v);
        addEdge(&g, v, u);
    }
    int d[N], f[N];
    dfs(&g, 1, 0, d, f);
    for (int i = 1; i <= n; i++) {
        g.f[i] = f[i];
    }
    scanf("%d", &q);
    for (int i = 1; i <= q; i++) {
        int x, y, z;
        scanf("%d%d%d", &x, &y, &z);
        int l = lca(&g, x, y, d, f);
        int ans = 0;
        if (d[x] + d[y] - 2 * d[l] <= z) {
            ans = m;
            for (int j = x; j != l; j = g.f[j]) {
                if (d[j] <= z) {
                    ans = m;
                    break;
                }
                ans = m - d[j] + 1;
            }
            for (int j = y; j != l; j = g.f[j]) {
                if (d[j] <= z) {
                    ans = m;
                    break;
                }
                ans = m - d[j] + 1;
            }
        } else {
            ans = m - d[l] + 1;
        }
        printf("%d\n", ans);
    }
    return 0;
}
