using namespace std;

const int MAX_N = 1e5 + 5;

int n, m, q;
vector<int> g[MAX_N];
int dist[MAX_N];
bool vis[MAX_N];

void bfs(int s) {
    queue<int> q;
    q.push(s);
    vis[s] = true;
    dist[s] = 0;
    while (!q.empty()) {
        int u = q.front();
        q.pop();
        for (int v : g[u]) {
            if (!vis[v]) {
                vis[v] = true;
                dist[v] = dist[u] + 1;
                q.push(v);
            }
        }
    }
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    cin >> n >> m;
    for (int i = 1; i <= m; i++) {
        int u, v;
        cin >> u >> v;
        g[u].push_back(v);
        g[v].push_back(u);
    }
    bfs(1);
    cin >> q;
    while (q--) {
        int x, y, z;
        cin >> x >> y >> z;
        int ans = 0;
        if (dist[x] + dist[y] + z - 2 <= m) {
            ans = max(dist[x], dist[y]);
        } else {
            int u = x;
            int v = y;
            if (dist[u] > dist[v]) {
                swap(u, v);
            }
            int d = dist[v] - dist[u];
            int c = z - d;
            if (c <= m) {
                ans = max(dist[u], dist[v] - c);
            } else {
                ans = m;
            }
        }
        cout << ans << '\n';
    }

    return 0;
}
