```java
import java.io.*;
import java.util.*;

public class Main {
    static int N = (int) 1e5 + 5;
    static int M = (int) 1e5 + 5;
    static int Q = (int) 1e5 + 5;
    static int[] fa = new int[N];
    static int[] sz = new int[N];
    static int[] dep = new int[N];
    static int[] ans = new int[Q];

    static int find(int x) {
        return x == fa[x] ? x : (fa[x] = find(fa[x]));
    }

    static void merge(int x, int y) {
        x = find(x);
        y = find(y);
        if (x == y) return;
        if (sz[x] < sz[y]) {
            int temp = x;
            x = y;
            y = temp;
        }
        fa[y] = x;
        sz[x] += sz[y];
        dep[x] = Math.max(dep[x], dep[y] + 1);
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        StringTokenizer st = new StringTokenizer(br.readLine());
        int n = Integer.parseInt(st.nextToken());
        int m = Integer.parseInt(st.nextToken());
        for (int i = 1; i <= n; i++) {
            fa[i] = i;
            sz[i] = 1;
        }
        for (int i = 1; i <= m; i++) {
            st = new StringTokenizer(br.readLine());
            int x = Integer.parseInt(st.nextToken());
            int y = Integer.parseInt(st.nextToken());
            merge(x, y);
        }
        int q = Integer.parseInt(br.readLine());
        for (int i = 1; i <= q; i++) {
            st = new StringTokenizer(br.readLine());
            int x = Integer.parseInt(st.nextToken());
            int y = Integer.parseInt(st.nextToken());
            int z = Integer.parseInt(st.nextToken());
            int l = Math.min(dep[find(x)], dep[find(y)]);
            int r = l + z - 1;
            ans[i] = Math.max(Math.max(find(x), find(y)), Math.max(l, r));
        }
        for (int i = 1; i <= q; i++) {
            System.out.println(ans[i]);
        }
    }
}
