    for (char c : D) {
        disliked.push_back(c - '0');
    }

    std::sort(disliked.begin(), disliked.end());

    int i = 0;
    while (i < 10 && std::find(disliked.begin(), disliked.end(), i) != disliked.end()) {
        i++;
    }

    int j = 0;
    while (j < 10 && std::find(disliked.begin(), disliked.end(), j) != disliked.end()) {
        j++;
    }

    int ans = 0;
    int cnt = 0;
    while (N > 0) {
        int digit = (N / (j * pow(10, cnt))) % j;
        if (digit == 0) {
            ans += N;
            break;
        }
        ans += digit * pow(10, cnt);
        N -= digit * pow(10, cnt);
        cnt++;
    }

    std::cout << ans << std::endl;

    return 0;
}
