    for (int i = 1; i <= H + W; i++) {
        fac[i] = fac[i - 1] * i % MOD;
    }

    long long ans = fac[H + W - 1];
    ans = ans * power(fac[H - 1], MOD - 2) % MOD;
    ans = ans * power(fac[W - 1], MOD - 2) % MOD;

    for (int i = 0; i < A; i++) {
