const int MOD = 1e9 + 7;

int main() {
    int n, x, y, z;
    cin >> n >> x >> y >> z;

    vector<vector<int>> dp(n + 1, vector<int>(n + 1, 0));
    vector<vector<int>> sum(n + 1, vector<int>(n + 1, 0));

    for (int i = 1; i <= n; ++i) {
        for (int j = 1; j <= i; ++j) {
            sum[i][j] = (sum[i - 1][j] + j) % MOD;
        }
    }

    for (int i = 1; i <= n; ++i) {
        for (int j = 1; j <= i; ++j) {
            for (int k = 1; k <= i - j; ++k) {
                for (int l = 1; l <= i - j - k; ++l) {
                    for (int m = 1; m <= i - j - k - l; ++m) {
                        if (sum[i][j] == x && sum[j][k] == y && sum[k][l] == z && sum[l][m] == 0 && sum[m][i - m] == 0) {
                            dp[i][j] = (dp[i][j] + 1) % MOD;
                        }
                    }
                }
            }
        }
    }

    int ans = 0;
    for (int i = 1; i <= n; ++i) {
        ans = (ans + dp[i][i]) % MOD;
    }

    for (int i = 1; i <= n; ++i) {
        for (int j = 1; j <= i; ++j) {
            for (int k = 1; k <= i - j; ++k) {
                for (int l = 1; l <= i - j - k; ++l) {
                    for (int m = 1; m <= i - j - k - l; ++m) {
                        if (sum[i][j] == x && sum[j][k] == y && sum[k][l] == z && sum[l][m] == 0 && sum[m][i - m] == 0) {
                            ans = (ans + dp[i][j] * pow(10, i - j - k - l - m)) % MOD;
                        }
                    }
                }
            }
        }
    }

    cout << ans << endl;

    return 0;
}
