def C(n, k):
    if k > n:
        return 0
    return mul(fact[n], mul(inv(fact[k]), inv(fact[n - k])))

def fact(n):
    f = [1] * (n + 1)
    for i in range(1, n + 1):
        f[i] = mul(f[i - 1], i)
    return f

