    scanf("%d %d %d %d", &H, &W, &A, &B);

    long long dp[2][H + 1];
    dp[0][0] = 1;
    for (int i = 1; i <= H; i++) {
        dp[0][i] = (dp[0][i - 1] * (W - (i > A ? 1 : 0))) % MOD;
    }

    dp[1][0] = 1;
    for (int i = 1; i <= W; i++) {
        dp[1][i] = (dp[1][i - 1] * (H - (i > B ? 1 : 0))) % MOD;
    }

    long long ans = 0;
    for (int i = 1; i <= H; i++) {
        for (int j = 1; j <= W; j++) {
            if (i > A || j > B) {
                ans = (ans + dp[0][i - 1] * dp[1][j - 1]) % MOD;
            }
        }
    }

