        if (b & 1) res = res * a % MOD;
        a = a * a % MOD;
        b >>= 1;
    }
    return res;
}

int main() {
    int H, W, A, B;
    cin >> H >> W >> A >> B;

    vector<long long> fac(H + 1), inv(H + 1);
    fac[0] = inv[0] = 1;
    for (int i = 1; i <= H; i++) fac[i] = fac[i - 1] * i % MOD;
    inv[H] = power(fac[H], MOD - 2);
    for (int i = H - 1; i >= 0; i--) inv[i] = inv[i + 1] * (i + 1) % MOD;

    long long ans = fac[H + W - 2] * inv[H - 1] % MOD * inv[W - 1] % MOD;
    for (int i = 1; i <= A; i++) {
        for (int j = 1; j <= B; j++) {
            int x = H - i + 1, y = W - j + 1;
            if (x + y - 2 >= 0) {
                ans = (ans - fac[x + y - 2] * inv[x - 1] % MOD * inv[y - 1] % MOD + MOD) % MOD;
            }
        }
    }
    cout << ans << endl;
    return 0;
