#define MAXN 100005

int main() {
    int N, M;
    scanf("%d %d", &N, &M);
    int A[MAXN];
    for (int i = 0; i < M; i++) {
        scanf("%d", &A[i]);
    }
    int sum = 0;
    for (int i = 0; i < M; i++) {
        sum += A[i];
    }
    if (sum != N) {
        printf("Impossible\n");
        return 0;
    }
    int max = 0;
    for (int i = 0; i < M; i++) {
        if (A[i] > max) {
            max = A[i];
        }
    }
    int max_index = 0;
    for (int i = 0; i < M; i++) {
        if (A[i] == max) {
            max_index = i;
            break;
        }
    }
    int a[N];
    int b[N];
    int index = 0;
    for (int i = 0; i < max_index; i++) {
        for (int j = 0; j < A[i]; j++) {
            a[index] = i + 1;
            index++;
        }
    }
    for (int i = max_index; i < M; i++) {
        for (int j = 0; j < A[i]; j++) {
            b[index] = i + 1;
            index++;
        }
    }
    for (int i = 0; i < N - max; i++) {
        a[index] = max_index + 1;
        index++;
    }
    printf("%d", a[0]);
    for (int i = 1; i < N; i++) {
        printf(" %d", a[i]);
    }
    printf("\n%d\n", N - max);
    for (int i = 0; i < N - max; i++) {
        printf("%d", b[i]);
        if (i < N - max - 1) {
            printf(" ");
        }
    }
    printf("\n");
    return 0;
}
