#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MOD 1000000007
#define MAXN 200005

typedef long long ll;

ll n, a[MAXN], b[MAXN], fac[MAXN], inv[MAXN];

ll qpow(ll x, ll k) {
    ll res = 1;
    while (k) {
        if (k & 1) res = res * x % MOD;
        x = x * x % MOD;
        k >>= 1;
    }
    return res;
}

ll C(ll n, ll m) {
    if (n < m) return 0;
    return fac[n] * inv[m] % MOD * inv[n - m] % MOD;
}

int main() {
    scanf("%lld", &n);
    for (ll i = 1; i <= n; i++) {
        scanf("%lld%lld", &a[i], &b[i]);
    }
    fac[0] = 1;
    for (ll i = 1; i <= n; i++) {
        fac[i] = fac[i - 1] * i % MOD;
    }
    inv[n] = qpow(fac[n], MOD - 2);
    for (ll i = n - 1; i >= 0; i--) {
        inv[i] = inv[i + 1] * (i + 1) % MOD;
    }
    ll ans = 0;
    for (ll i = 1; i <= n; i++) {
        for (ll j = i + 1; j <= n; j++) {
            ll x = (a[i] + a[j]) * (a[i] + a[j] + 1) / 2 % MOD;
            ll y = (b[i] + b[j]) * (b[i] + b[j] + 1) / 2 % MOD;
            ll z = (a[i] + a[j] + b[i] + b[j]) * (a[i] + a[j] + b[i] + b[j] + 1) / 2 % MOD;
            ans = (ans + C(n - 2, i - 1) * C(n - 2, j - i - 1) * C(2 * (x + y) - 1, 2 * x - 1) % MOD * C(2 * (z + x + y) - 1, 2 * z - 1) % MOD) % MOD;
        }
    }
    printf("%lld\n", ans);
    return 0;
}
