public class Main {
    static final int MOD = 1000000007;
    static int n;
    static int[][] a;
    static long[] fac;
    static long[] inv;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        n = Integer.parseInt(br.readLine());
        a = new int[n][2];
        for (int i = 0; i < n; i++) {
            StringTokenizer st = new StringTokenizer(br.readLine());
            a[i][0] = Integer.parseInt(st.nextToken());
            a[i][1] = Integer.parseInt(st.nextToken());
        }
        fac = new long[n + 1];
        inv = new long[n + 1];
        fac[0] = 1;
        for (int i = 1; i <= n; i++) {
            fac[i] = fac[i - 1] * i % MOD;
        }
        inv[n] = pow(fac[n], MOD - 2);
        for (int i = n - 1; i >= 0; i--) {
            inv[i] = inv[i + 1] * (i + 1) % MOD;
        }
        long ans = 0;
        for (int i = 0; i < n; i++) {
            for (int j = i + 1; j < n; j++) {
                long x = (a[i][0] + a[j][0]) * (a[i][0] + a[j][0] - 1) / 2 % MOD;
                long y = (a[i][1] + a[j][1]) * (a[i][1] + a[j][1] - 1) / 2 % MOD;
                long z = a[i][0] * a[i][1] % MOD * a[j][0] * a[j][1] % MOD;
                long t = fac[2] * fac[n - 2] % MOD * inv[i] % MOD * inv[j - i - 1] % MOD * inv[n - j - 1] % MOD;
                ans = (ans + t * x % MOD * y % MOD * z % MOD) % MOD;
            }
        }
        pw.println(ans);
        pw.close();
    }

    static long pow(long a, long b) {
        long res = 1;
        while (b > 0) {
            if ((b & 1) == 1) {
                res = res * a % MOD;
            }
            a = a * a % MOD;
            b >>= 1;
        }
        return res;
    }
}
