#include <stdio.h>
#include <stdlib.h>

#define N 500000
#define K 500000

int P[N + 1];
int pos[N + 1];

void swap(int *a, int *b) {
    int temp = *a;
    *a = *b;
    *b = temp;
}

void solve(int n, int k) {
    int i, j, p, q;

    for (i = 1; i <= n; i++) {
        pos[P[i]] = i;
    }

    for (i = 1; i <= n; i++) {
        if (pos[i] != i) {
            p = i;
            q = pos[i];
            while (q > i + k) {
                swap(&P[p], &P[q]);
                p++;
                q--;
            }
            for (j = i + 1; j <= p; j++) {
                pos[P[j]] = j;
            }
        }
    }

    for (i = 1; i <= n; i++) {
        printf("%d ", P[i]);
    }
    printf("\n");
}

int main() {
    int n, k, i;

    scanf("%d %d", &n, &k);
    for (i = 1; i <= n; i++) {
        scanf("%d", &P[i]);
    }
    solve(n, k);
    return 0;
}
