#include <iostream>
#include <cmath>

int gcd(int a, int b) {
    if (b == 0) return a;
    return gcd(b, a % b);
}

int main() {
    int p, n;
    while (std::cin >> p >> n) {
        if (p == 0 && n == 0) break;

        int x = 1, y = 1;
        int u = 1, v = 1;

        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= n; j++) {
                if (i * i * j > p * n * n) break;
                if (i * i * j == p * n * n) {
                    x = i * n;
                    y = i;
                    break;
                }
                if (i * i * j > p * (j - 1) * (j - 1)) {
                    u = i * j;
                    v = i;
                }
            }
        }

        int g = gcd(x, y);
        x /= g;
        y /= g;
        g = gcd(u, v);
        u /= g;
        v /= g;

        std::cout << x << "/" << y << " " << u << "/" << v << std::endl;
    }

    return 0;
}
