import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);

        while (true) {
            int p = scanner.nextInt();
            int n = scanner.nextInt();

            if (p == 0 && n == 0) {
                break;
            }

            int x = 0;
            int y = 0;
            int u = 0;
            int v = 0;

            for (int i = 1; i <= n; i++) {
                for (int j = 1; j <= n; j++) {
                    if (i * i > p) {
                        break;
                    }
                    if (j * j > p) {
                        break;
                    }
                    if (i * j > p) {
                        break;
                    }
                    if (i * j == p) {
                        continue;
                    }
                    if (i * j < p && i * i < p && j * j < p) {
                        if (x == 0 || (double) i / j > (double) x / y) {
                            x = i;
                            y = j;
                        }
                        if (u == 0 || (double) i / j < (double) u / v) {
                            u = i;
                            v = j;
                        }
                    }
                }
            }

            System.out.println(x + "/" + y + " " + u + "/" + v);
        }
    }
}
