#include <iostream>
#include <vector>
#include <string>
#include <sstream>

int main() {
    std::string line;
    while (std::getline(std::cin, line)) {
        std::istringstream iss(line);
        std::vector<int> a, b;
        int num;
        for (int i = 0; i < 4; ++i) {
            iss >> num;
            a.push_back(num);
            b.push_back(num);
        }

        int hit = 0, blow = 0;
        for (int i = 0; i < 4; ++i) {
            if (a[i] == b[i]) {
                hit++;
                b[i] = -1; // mark as visited
            }
        }

        for (int i = 0; i < 4; ++i) {
            if (b[i] != -1) {
                for (int j = 0; j < 4; ++j) {
                    if (b[i] == a[j] && b[i] != -1) {
                        blow++;
                        b[i] = -1; // mark as visited
                        break;
                    }
                }
            }
        }

        std::cout << hit << " " << blow << std::endl;
    }

    return 0;
}
