#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAX_COMMANDS 1024
#define MAX_COMMAND_LENGTH 10

typedef struct {
    int top;
    int north;
    int east;
    int south;
    int west;
} Die;

int main() {
    Die die = {1, 2, 3, 4, 5, 6};
    char command[MAX_COMMAND_LENGTH];
    int num_commands;

    while (scanf("%d", &num_commands) == 1) {
        for (int i = 0; i < num_commands; i++) {
            scanf("%s", command);
            if (strcmp(command, "north") == 0) {
                int temp = die.top;
                die.top = die.north;
                die.north = die.east;
                die.east = die.south;
                die.south = die.west;
                die.west = temp;
            } else if (strcmp(command, "east") == 0) {
                int temp = die.top;
                die.top = die.east;
                die.east = die.south;
                die.south = die.west;
                die.west = temp;
            } else if (strcmp(command, "south") == 0) {
                int temp = die.top;
                die.top = die.south;
                die.south = die.west;
                die.west = die.east;
                die.east = temp;
            } else if (strcmp(command, "west") == 0) {
                int temp = die.top;
                die.top = die.west;
                die.west = die.east;
                die.east = die.south;
                die.south = temp;
            }
        }
        printf("%d\n", die.top);
    }
    return 0;
}
