            if (line.equals("0")) {
                break;
            }
            int n = Integer.parseInt(line);
            int[] top = new int[]{1, 2, 3};
            int[] bottom = new int[]{6, 5, 4};
            for (int i = 0; i < n; i++) {
                String command = scanner.nextLine();
                int[] temp = top;
                top = bottom;
                bottom = temp;
                switch (command) {
                    case "north":
                        int[] temp2 = top;
                        top = bottom;
                        bottom = temp2;
                        break;
                    case "east":
                        int[] temp3 = top;
                        top = bottom;
                        bottom = new int[]{temp3[0], temp3[2], temp3[1]};
                        break;
                    case "south":
