#include <iostream>
#include <vector>
#include <string>
#include <algorithm>

using namespace std;

struct Point {
    int x, y;
};

struct Trapezoid {
    int x1, x2, y1, y2;
    int area;
};

int main() {
    int h;
    while (cin >> h && h != 0) {
        vector<string> picture(h);
        for (int i = 0; i < h; i++) {
            cin >> picture[i];
        }

        vector<Trapezoid> trapezoids;
        for (int i = 0; i < h; i++) {
            for (int j = 0; j < picture[i].length(); j++) {
                if (picture[i][j] == '*') {
                    int x = j, y = i;
                    int x1 = x, x2 = x, y1 = y, y2 = y;
                    while (x > 0 && picture[y][x - 1] == '*') {
                        x--;
                    }
                    while (x < picture[y].length() - 1 && picture[y][x + 1] == '*') {
                        x++;
                    }
                    while (y > 0 && picture[y - 1][x] == '*') {
                        y--;
                    }
                    while (y < h - 1 && picture[y + 1][x] == '*') {
                        y++;
                    }
                    x1 = min(x1, x);
                    x2 = max(x2, x);
                    y1 = min(y1, y);
                    y2 = max(y2, y);
                    bool found = false;
                    for (int k = 0; k < trapezoids.size(); k++) {
                        if (trapezoids[k].x1 <= x1 && trapezoids[k].x2 >= x2 && trapezoids[k].y1 <= y1 && trapezoids[k].y2 >= y2) {
                            found = true;
                            break;
                        }
                    }
                    if (!found) {
                        int area = (x2 - x1 + 1) * (y2 - y1 + 1);
                        for (int k = 0; k < trapezoids.size(); k++) {
                            if (trapezoids[k].x1 <= x1 && trapezoids[k].x2 >= x2 && trapezoids[k].y1 <= y1 && trapezoids[k].y2 >= y2) {
                                area -= trapezoids[k].area;
                            }
                        }
                        trapezoids.push_back({x1, x2, y1, y2, area});
                    }
                }
            }
        }

        sort(trapezoids.begin(), trapezoids.end(), [](const Trapezoid& a, const Trapezoid& b) {
            return a.area < b.area;
        });

        int count = 0;
        for (int i = 0; i < trapezoids.size(); i++) {
            if (i == 0 || trapezoids[i].area != trapezoids[i - 1].area) {
                cout << trapezoids[i].area << " " << count << endl;
                count = 1;
            } else {
                count++;
            }
        }
        cout << "----------" << endl;
    }
    return 0;
}
