import sys

def find_trapezoids(height, picture):
    trapezoids = []
    for i in range(height):
        row = picture[i]
        for j in range(len(row)):
            if row[j] == '*':
                x, y = j, i
                width = 1
                while x > 0 and row[x-1] == '*':
                    x -= 1
                    width += 1
                height = 1
                while y > 0 and picture[y-1][x] == '*':
                    y -= 1
                    height += 1
                area = width * height
                found = False
                for trapezoid in trapezoids:
                    if (x - trapezoid['x'] <= trapezoid['width'] and
                        x + width - 1 >= trapezoid['x'] and
                        y - trapezoid['y'] <= trapezoid['height'] and
                        y + height - 1 >= trapezoid['y']):
                        area += trapezoid['area']
                        found = True
                        break
                if not found:
                    trapezoids.append({'x': x, 'y': y, 'width': width, 'height': height, 'area': area})
    return trapezoids

def main():
    pictures = []
    while True:
        try:
            height = int(sys.stdin.readline().strip())
            picture = []
            for _ in range(height):
                row = sys.stdin.readline().strip()
                picture.append(row)
            pictures.append(picture)
        except ValueError:
            break
    for i in range(len(pictures) - 1):
        trapezoids = []
        for picture in pictures[i]:
            trapezoids.extend(find_trapezoids(len(picture), picture))
        trapezoids.sort(key=lambda x: x['area'])
        areas = [t['area'] for t in trapezoids]
        counts = [areas.count(a) for a in set(areas)]
        for a, c in zip(areas, counts):
            print(f"{a} {c}")
        print("-" * 10)
    trapezoids = []
    for picture in pictures[-1]:
        trapezoids.extend(find_trapezoids(len(picture), picture))
    trapezoids.sort(key=lambda x: x['area'])
    areas = [t['area'] for t in trapezoids]
    counts = [areas.count(a) for a in set(areas)]
    for a, c in zip(areas, counts):
        print(f"{a} {c}")

if __name__ == "__main__":
    main()
